do
    local Core = {}
    Core.resolve = resolve or Resolve()
    Core.fusion = Core.resolve and Core.resolve:Fusion()
    Core.ui = Core.fusion and Core.fusion.UIManager
    Core.dispatcher = Core.ui and bmd.UIDispatcher(Core.ui)

    if not Core.dispatcher then
        print(string.format("%s: 无法初始化 Fusion UI. (Resolve/Fusion API 不可用)", App.Config.SCRIPT_NAME or "Script"))
        return
    end

    Core.timeoutHandlers = {}
    Core._timeoutFallback = Core.dispatcher.On.Timeout

    function Core.registerTimeoutHandler(id, fn)
        if not id or id == "" or type(fn) ~= "function" then
            return false, "invalid_handler"
        end
        Core.timeoutHandlers[id] = fn
        return true
    end

    function Core.removeTimeoutHandler(id)
        if not id then return end
        Core.timeoutHandlers[id] = nil
    end

    function Core.dispatchTimeout(ev)
        local handled = false
        if Core._timeoutFallback then
            local ok, res = pcall(Core._timeoutFallback, ev)
            if ok then
                handled = res == true
            else
                print(string.format("[Timeout] fallback handler error: %s", tostring(res)))
            end
        end
        local who = ev and (ev.who or ev.ID or ev.Name or ev.TimerID or ev.TimerId)
        if who and Core.timeoutHandlers[who] then
            local ok, res = pcall(Core.timeoutHandlers[who], ev)
            if ok then
                if res == nil then
                    handled = true
                else
                    handled = handled or (res == true)
                end
            else
                print(string.format("[Timeout] handler error (%s): %s", tostring(who), tostring(res)))
            end
        elseif not who then
            for id, fn in pairs(Core.timeoutHandlers) do
                local ok, res = pcall(fn, ev)
                if ok then
                    if res == nil or res == true then
                        handled = true
                    end
                else
                    print(string.format("[Timeout] handler error (%s): %s", tostring(id), tostring(res)))
                end
            end
        end
        return handled
    end

    Core.dispatcher.On.Timeout = function(ev)
        return Core.dispatchTimeout(ev)
    end

    App.Core = Core
end

