do
    local Utils = {}
    local Config = App.Config

    -- 加载json模块
    local json = require("dkjson")

    Utils.SEP = package.config:sub(1,1)
    Utils.IS_WINDOWS = (Utils.SEP == "\\")

    -- Windows helpers (ffi): hidden command + UTF-8 safe file IO
    local runHiddenWindowsCommand
    local runHiddenWindowsCommandAsync
    local runHiddenWindowsCommandAsyncRaw
    local winfs
    local runShellCommand
    local runShellCommandAsync
    do
        if Utils.IS_WINDOWS then
            local okFfi, ffi = pcall(require, "ffi")
            if okFfi and ffi then
                local okKernel, kernel32 = pcall(ffi.load, "kernel32")
                local okMsvcrt, msvcrt = pcall(ffi.load, "msvcrt")

                if okKernel and kernel32 then
                    local CP_UTF8 = 65001
                    local CREATE_NO_WINDOW = 0x08000000
                    local STARTF_USESHOWWINDOW = 0x00000001
                    local INFINITE = 0xFFFFFFFF

                    pcall(ffi.cdef, [[
                        typedef unsigned short WORD;
                        typedef unsigned long DWORD;
                        typedef int BOOL;
                        typedef void* HANDLE;
                        typedef wchar_t* LPWSTR;
                        typedef const wchar_t* LPCWSTR;
                        typedef void* LPVOID;
                        typedef unsigned char BYTE;

                        typedef struct _STARTUPINFOW {
                            DWORD cb;
                            LPWSTR lpReserved;
                            LPWSTR lpDesktop;
                            LPWSTR lpTitle;
                            DWORD dwX;
                            DWORD dwY;
                            DWORD dwXSize;
                            DWORD dwYSize;
                            DWORD dwXCountChars;
                            DWORD dwYCountChars;
                            DWORD dwFillAttribute;
                            DWORD dwFlags;
                            WORD wShowWindow;
                            WORD cbReserved2;
                            BYTE *lpReserved2;
                            HANDLE hStdInput;
                            HANDLE hStdOutput;
                            HANDLE hStdError;
                        } STARTUPINFOW;

                        typedef struct _PROCESS_INFORMATION {
                            HANDLE hProcess;
                            HANDLE hThread;
                            DWORD dwProcessId;
                            DWORD dwThreadId;
                        } PROCESS_INFORMATION;

                        BOOL CreateProcessW(
                            LPCWSTR lpApplicationName,
                            LPWSTR lpCommandLine,
                            LPVOID lpProcessAttributes,
                            LPVOID lpThreadAttributes,
                            BOOL bInheritHandles,
                            DWORD dwCreationFlags,
                            LPVOID lpEnvironment,
                            LPCWSTR lpCurrentDirectory,
                            STARTUPINFOW *lpStartupInfo,
                            PROCESS_INFORMATION *lpProcessInformation
                        );

                        DWORD WaitForSingleObject(HANDLE hHandle, DWORD dwMilliseconds);
                        BOOL GetExitCodeProcess(HANDLE hProcess, DWORD *lpExitCode);
                        BOOL CloseHandle(HANDLE hObject);
                        DWORD GetLastError(void);
                        int MultiByteToWideChar(unsigned int CodePage, DWORD dwFlags,
                                                const char *lpMultiByteStr, int cbMultiByte,
                                                wchar_t *lpWideCharStr, int cchWideChar);
                    ]])

                    local function utf8ToWideBuffer(str)
                        local okReq, required = pcall(function()
                            return kernel32.MultiByteToWideChar(CP_UTF8, 0, str, -1, nil, 0)
                        end)
                        if not okReq or required == 0 then
                            return nil
                        end
                        local buffer = ffi.new("wchar_t[?]", required)
                        local okFill, filled = pcall(function()
                            return kernel32.MultiByteToWideChar(CP_UTF8, 0, str, -1, buffer, required)
                        end)
                        if not okFill or filled == 0 then
                            return nil
                        end
                        return buffer
                    end

                    local okCreate = pcall(function() return kernel32.CreateProcessW end)
                    local okM2W = pcall(function() return kernel32.MultiByteToWideChar end)
                    if okCreate and okM2W then
                        runHiddenWindowsCommand = function(command)
                            local comspec = os.getenv("COMSPEC") or "C:\\Windows\\System32\\cmd.exe"
                            local fullCommand = string.format('"%s" /c %s', comspec, command)
                            local cmdBuffer = utf8ToWideBuffer(fullCommand)
                            if not cmdBuffer then return false, "command_encoding_failed" end

                            local startupInfo = ffi.new("STARTUPINFOW")
                            startupInfo.cb = ffi.sizeof(startupInfo)
                            startupInfo.dwFlags = STARTF_USESHOWWINDOW
                            startupInfo.wShowWindow = 0

                            local processInfo = ffi.new("PROCESS_INFORMATION")
                            local created = kernel32.CreateProcessW(
                                nil, cmdBuffer, nil, nil, false,
                                CREATE_NO_WINDOW, nil, nil,
                                startupInfo, processInfo
                            )

                            if created == 0 then return false, kernel32.GetLastError() end

                            kernel32.WaitForSingleObject(processInfo.hProcess, INFINITE)
                            local exitCodeArr = ffi.new("DWORD[1]", 0)
                            kernel32.GetExitCodeProcess(processInfo.hProcess, exitCodeArr)
                            kernel32.CloseHandle(processInfo.hProcess)
                            kernel32.CloseHandle(processInfo.hThread)

                            return exitCodeArr[0] == 0, exitCodeArr[0]
                        end

                        runHiddenWindowsCommandAsync = function(command)
                            local comspec = os.getenv("COMSPEC") or "C:\\Windows\\System32\\cmd.exe"
                            local fullCommand = string.format('"%s" /c %s', comspec, command)
                            local cmdBuffer = utf8ToWideBuffer(fullCommand)
                            if not cmdBuffer then return false, "command_encoding_failed" end

                            local startupInfo = ffi.new("STARTUPINFOW")
                            startupInfo.cb = ffi.sizeof(startupInfo)
                            startupInfo.dwFlags = STARTF_USESHOWWINDOW
                            startupInfo.wShowWindow = 0

                            local processInfo = ffi.new("PROCESS_INFORMATION")
                            local created = kernel32.CreateProcessW(
                                nil, cmdBuffer, nil, nil, false,
                                CREATE_NO_WINDOW, nil, nil,
                                startupInfo, processInfo
                            )

                            if created == 0 then return false, kernel32.GetLastError() end

                            kernel32.CloseHandle(processInfo.hProcess)
                            kernel32.CloseHandle(processInfo.hThread)

                            return true, processInfo.dwProcessId
                        end

                        runHiddenWindowsCommandAsyncRaw = function(command)
                            local cmdBuffer = utf8ToWideBuffer(command)
                            if not cmdBuffer then return false, "command_encoding_failed" end

                            local startupInfo = ffi.new("STARTUPINFOW")
                            startupInfo.cb = ffi.sizeof(startupInfo)
                            startupInfo.dwFlags = STARTF_USESHOWWINDOW
                            startupInfo.wShowWindow = 0

                            local processInfo = ffi.new("PROCESS_INFORMATION")
                            local created = kernel32.CreateProcessW(
                                nil, cmdBuffer, nil, nil, false,
                                CREATE_NO_WINDOW, nil, nil,
                                startupInfo, processInfo
                            )

                            if created == 0 then return false, kernel32.GetLastError() end

                            kernel32.CloseHandle(processInfo.hProcess)
                            kernel32.CloseHandle(processInfo.hThread)

                            return true, processInfo.dwProcessId
                        end
                    end
                end

                if okKernel and okMsvcrt and kernel32 and msvcrt then
                    local CP_UTF8 = 65001

                    local okSizeT = pcall(function() return ffi.sizeof("size_t") end)
                    if not okSizeT then
                        pcall(ffi.cdef, [[ typedef unsigned long size_t; ]])
                    end

                    pcall(ffi.cdef, [[
                        typedef struct _iobuf FILE;
                        FILE* _wfopen(const wchar_t* filename, const wchar_t* mode);
                        int fclose(FILE* stream);
                        size_t fread(void* ptr, size_t size, size_t count, FILE* stream);
                        size_t fwrite(const void* ptr, size_t size, size_t count, FILE* stream);
                        int fseek(FILE* stream, long offset, int origin);
                        long ftell(FILE* stream);
                    ]])

                    local okM2W = pcall(function() return kernel32.MultiByteToWideChar end)
                    local okWfopen = pcall(function() return msvcrt._wfopen end)
                    local okFclose = pcall(function() return msvcrt.fclose end)
                    local okFseek = pcall(function() return msvcrt.fseek end)
                    local okFtell = pcall(function() return msvcrt.ftell end)
                    local okFread = pcall(function() return msvcrt.fread end)
                    local okFwrite = pcall(function() return msvcrt.fwrite end)
                    local canWinfs = okM2W and okWfopen and okFclose and okFseek and okFtell and okFread and okFwrite

                    if canWinfs then
                        local function utf8ToWide(str)
                            local okReq, needed = pcall(function()
                                return kernel32.MultiByteToWideChar(CP_UTF8, 0, str, -1, nil, 0)
                            end)
                            if not okReq or needed == 0 then
                                return nil
                            end
                            local buf = ffi.new("wchar_t[?]", needed)
                            local okFill, filled = pcall(function()
                                return kernel32.MultiByteToWideChar(CP_UTF8, 0, str, -1, buf, needed)
                            end)
                            if not okFill or filled == 0 then
                                return nil
                            end
                            return buf
                        end

                        local function openWide(path, mode)
                            local wpath = utf8ToWide(path or "")
                            local wmode = utf8ToWide(mode or "rb")
                            if not wpath or not wmode then
                                return nil, "path_encoding_failed"
                            end
                            local f = msvcrt._wfopen(wpath, wmode)
                            if f == nil then
                                return nil, "wfopen_failed"
                            end
                            return f
                        end

                        winfs = {}

                        function winfs.readFile(path)
                            local f, err = openWide(path, "rb")
                            if not f then
                                return nil, err
                            end
                            msvcrt.fseek(f, 0, 2)
                            local size = tonumber(msvcrt.ftell(f)) or 0
                            if size < 0 then
                                size = 0
                            end
                            msvcrt.fseek(f, 0, 0)
                            if size == 0 then
                                msvcrt.fclose(f)
                                return ""
                            end
                            local buf = ffi.new("char[?]", size)
                            local read = tonumber(msvcrt.fread(buf, 1, size, f)) or 0
                            msvcrt.fclose(f)
                            return ffi.string(buf, read)
                        end

                        function winfs.writeFile(path, content)
                            local f, err = openWide(path, "wb")
                            if not f then
                                return false, err
                            end
                            local data = content or ""
                            local len = #data
                            local written = tonumber(msvcrt.fwrite(data, 1, len, f)) or 0
                            msvcrt.fclose(f)
                            if written ~= len then
                                return false, "write_failed"
                            end
                            return true, nil
                        end

                        function winfs.fileExists(path)
                            local f = select(1, openWide(path, "rb"))
                            if f then
                                msvcrt.fclose(f)
                                return true
                            end
                            return false
                        end

                        function winfs.getFileSize(path)
                            local f = select(1, openWide(path, "rb"))
                            if not f then
                                return nil
                            end
                            msvcrt.fseek(f, 0, 2)
                            local size = tonumber(msvcrt.ftell(f))
                            msvcrt.fclose(f)
                            return size
                        end
                    end
                end
            end
        end

        runShellCommand = function(command)
            if Utils.IS_WINDOWS and type(runHiddenWindowsCommand) == "function" then
                local ok, code = runHiddenWindowsCommand(command)
                if not ok then
                    print(string.format("Command failed (exit=%s)", tostring(code)))
                end
                -- 保持第1返回值是布尔；新增第2返回值为退出码
                return ok == true, tonumber(code) or 0
            end

            -- *nix：os.execute 在不同 Lua 版本返回形式不同，这里做了全覆盖
            local res, how, code = os.execute(command)
            if type(res) == "number" then
                return res == 0, res
            end
            if res == true then
                if how == "exit" then return (code or 0) == 0, (code or 0) end
                return true, 0
            end
            return false, (type(code) == "number" and code or 1)
        end

        runShellCommandAsync = function(command)
            if Utils.IS_WINDOWS and type(runHiddenWindowsCommandAsync) == "function" then
                local ok, code = runHiddenWindowsCommandAsync(command)
                if not ok then
                    print(string.format("Command failed (async, code=%s)", tostring(code)))
                end
                return ok == true, tonumber(code) or 0
            end

            if Utils.IS_WINDOWS then
                local res = os.execute(string.format('start "" /B cmd /c %s', command))
                if type(res) == "number" then
                    return res == 0, res
                end
                if res == true then
                    return true, 0
                end
                return false, 1
            end

            -- Preserve caller redirections (statusPath/errPath); only detach stdin.
            local handle = io.popen(command .. " </dev/null &", "r")
            if handle then
                handle:close()
                return true, 0
            end
            return false, 1
        end

    end
    Utils.runShellCommand = runShellCommand
    Utils.runShellCommandAsync = runShellCommandAsync

    -- 字符串工具
    function Utils.trim(s)
        if type(s) ~= "string" then return "" end
        return (s:gsub("^%s+", ""):gsub("%s+$", ""))
    end

    function Utils.urlEncode(str)
        if not str then return "" end
        return tostring(str):gsub("([^%w%-_%.%~])", function(c)
            return string.format("%%%02X", string.byte(c))
        end)
    end

    -- 路径工具
    function Utils.scriptDir()
        local source = debug.getinfo(1, "S").source
        if source:sub(1, 1) == "@" then
            source = source:sub(2)
        end
        local pattern = "(.*" .. Utils.SEP .. ")"
        if Utils.IS_WINDOWS then
            pattern = "(.*[\\/])"
        end
        return source:match(pattern) or ""
    end

    function Utils.joinPath(a, b)
        if a == "" then return b end
        if a:sub(-1) == Utils.SEP then return a .. b end
        return a .. Utils.SEP .. b
    end

    function Utils.getConfigDir()
        return Utils.joinPath(Utils.scriptDir(), "config")
    end

    function Utils.getTempDir()
        return Utils.joinPath(Utils.scriptDir(), "temp")
    end

    function Utils.fileExists(path)
        if Utils.IS_WINDOWS and winfs and winfs.fileExists then
            return winfs.fileExists(path)
        end
        local f = io.open(path, "rb")
        if f then
            f:close()
            return true
        end
        return false
    end

    function Utils.getFileSize(path)
        if Utils.IS_WINDOWS and winfs and winfs.getFileSize then
            return tonumber(winfs.getFileSize(path)) or 0
        end
        local f = io.open(path, "rb")
        if not f then
            return 0
        end
        local size = f:seek("end")
        f:close()
        return size or 0
    end

    function Utils.readFile(path)
        if Utils.IS_WINDOWS and winfs and winfs.readFile then
            return winfs.readFile(path)
        end
        local f, err = io.open(path, "rb")
        if not f then
            return nil, err
        end
        local content = f:read("*a")
        f:close()
        return content or ""
    end

    function Utils.writeFile(path, content)
        if Utils.IS_WINDOWS and winfs and winfs.writeFile then
            return winfs.writeFile(path, content)
        end
        local f, err = io.open(path, "wb")
        if not f then
            return false, err
        end
        f:write(content or "")
        f:close()
        return true
    end

    function Utils.ensureDir(path)
        if path == "" then return true end
        if bmd and bmd.fileexists and bmd.fileexists(path) then return true end
        if Utils.IS_WINDOWS then
            if not Utils.runShellCommand(string.format('if not exist "%s" mkdir "%s"', path, path)) then
                print("Failed to create directory: " .. path)
                return false
            end
        else
            local escaped = path:gsub("'", "'\\''")
            if not Utils.runShellCommand("mkdir -p '" .. escaped .. "'") then
                print("Failed to create directory: " .. path)
                return false
            end
        end
        return true
    end

    -- 调试文件清理（允许传入 table 或字符串）
    function Utils.cleanupDebugPaths(paths)
        if not paths then return end
        if type(paths) == "string" then
            if paths ~= "" and Utils.fileExists(paths) then
                os.remove(paths)
            end
            return
        end
        if type(paths) ~= "table" then return end
        for _, p in pairs(paths) do
            if type(p) == "string" then
                if p ~= "" and Utils.fileExists(p) then
                    os.remove(p)
                end
            elseif type(p) == "table" then
                Utils.cleanupDebugPaths(p)
            end
        end
    end

    function Utils.makeTempPath(ext)
        local dir = Utils.getTempDir()
        Utils.ensureDir(dir)
        local ts  = tostring(os.time())
        local rnd = string.format("%06d", math.random(0, 999999))
        local base = string.format("tmp_%s_%s", ts, rnd)
        if ext and ext ~= "" then
            if not ext:match("^%.") then ext = "." .. ext end
        else
            ext = ""
        end
        local path = Utils.joinPath(dir, base .. ext)
        local i = 0
        while Utils.fileExists(path) do
            i = i + 1
            path = Utils.joinPath(dir, base .. "_" .. i .. ext)
            if i > 1000 then return nil, "temp_name_exhausted" end
        end
        return path
    end

    function Utils.sanitizeFilename(name)
        name = tostring(name or "image")
        name = name:gsub("[%c%z]", ""):gsub("[/\\:%*%?%\"]", "_"):gsub("[<>|]", "_")
        name = name:gsub("%s+", "_"):gsub("^_+", ""):gsub("_+$", "")
        if name == "" then name = "image" end
        return name
    end

    function Utils.maskToken(token)
        token = tostring(token or "")
        if token == "" then return "未设置" end
        local len = #token
        if len <= 4 then
            return string.rep("*", len)
        end
        local masked = string.rep("*", len - 4) .. token:sub(-4)
        return masked
    end

    function Utils.maskHeaderValue(name, value)
        local lower = tostring(name or ""):lower()
        local str = tostring(value or "")
        local sensitiveHeaders = {
            ["authorization"] = true,
            ["apikey"] = true,
            ["api-key"] = true,
            ["x-api-key"] = true,
            ["x-goog-api-key"] = true,
            ["x-rapidapi-key"] = true,
        }
        local isSensitive = sensitiveHeaders[lower] == true
        if not isSensitive and (lower:find("token") or lower:find("secret")) then
            isSensitive = true
        end
        if lower == "authorization" then
            local prefix, token = str:match("^(%S+)%s+(.+)$")
            if prefix and token then
                return string.format("%s %s", prefix, Utils.maskToken(token))
            end
            return Utils.maskToken(str)
        end
        if isSensitive then
            return Utils.maskToken(str)
        end
        return str
    end

    function Utils.truncate(str, limit)
        str = tostring(str or "")
        limit = limit or 200
        if #str <= limit then
            return str
        end
        return str:sub(1, limit) .. "...(truncated)"
    end

    function Utils.sanitizeHeaderValue(value)
        value = tostring(value or "")
        value = value:gsub("[\r\n]", " ")
        return value
    end

    function Utils.escapeCurlConfigValue(value)
        value = tostring(value or "")
        value = value:gsub("\\", "\\\\")
        value = value:gsub("\"", "\\\"")
        value = value:gsub("[\r\n]", " ")
        return value
    end

    function Utils.writeCurlConfig(opts)
        opts = opts or {}
        local path, err = Utils.makeTempPath("curl")
        if not path then
            return nil, err or "temp_failed"
        end
        local lines = {}
        local function addLine(key, val)
            table.insert(lines, string.format('%s = "%s"', key, Utils.escapeCurlConfigValue(val)))
        end
        if opts.request and opts.request ~= "" then
            addLine("request", opts.request)
        end
        if opts.url and opts.url ~= "" then
            addLine("url", opts.url)
        end
        if opts.output and opts.output ~= "" then
            addLine("output", opts.output)
        end
        if opts.dataBinary and opts.dataBinary ~= "" then
            table.insert(lines, string.format('data-binary = "@%s"', Utils.escapeCurlConfigValue(opts.dataBinary)))
        end
        if type(opts.headers) == "table" then
            for _, header in ipairs(opts.headers) do
                if header and header ~= "" then
                    addLine("header", header)
                end
            end
        end
        local ok = Utils.writeFile(path, table.concat(lines, "\n"))
        if not ok then
            os.remove(path)
            return nil, "config_write_failed"
        end
        return path
    end

