    local function estimate_decoded_size(payload)
        local padding = 0
        if payload:sub(-2) == "==" then
            padding = 2
        elseif payload:sub(-1) == "=" then
            padding = 1
        end
        local blocks = math.floor(#payload / 4)
        return math.max(0, blocks * 3 - padding)
