        local winFlags = string.format('--http1.1 --no-keepalive -sS -L -N -4 -A "%s"', userAgent)
        -- 移除 limit-rate 限速，避免服务器因慢速下载超时断开
        -- 增加 tcp-keepalive 参数保持连接活跃
        local macFlags = string.format(
        '--http1.1 -sS -L -g ' ..
        '--connect-timeout 30 ' ..           -- 增加连接超时
