        if sep == "\\" then
            curlCommand = string.format(
                'curl %s -m %d --config "%s" -w "%%%%{http_code}"',
                winFlags, maxTime, configPath
            )
            redirected = string.format('%s > "%s" 2>"%s"', curlCommand, statusPath, errPath)
        else
