    local function system_decode_base64(payload, dest_path, expected_bytes)
        if type(payload) ~= "string" or payload == "" then
            return false
        end
        local tempB64, err = Utils.makeTempPath("b64")
        if not tempB64 then
            return false, err
        end
        local b64Content
        if Utils.IS_WINDOWS then
            local lines = {}
            local idx, len = 1, #payload
            while idx <= len do
                lines[#lines + 1] = payload:sub(idx, idx + 63)
                idx = idx + 64
            end
            b64Content = table.concat(lines, "\n") .. "\n"
        else
            b64Content = payload
            if b64Content:sub(-1) ~= "\n" then
                b64Content = b64Content .. "\n"
            end
        end
        local okWrite, werr = Utils.writeFile(tempB64, b64Content)
        if not okWrite then
            return false, "temp_write_failed:" .. tostring(werr)
        end

        local commands = {}
        if Utils.IS_WINDOWS then
            local function win_quote(path)
                return '"' .. tostring(path or ""):gsub('"', '""') .. '"'
            end
            local function ps_quote(path)
                return "'" .. tostring(path or ""):gsub("'", "''") .. "'"
            end
            table.insert(commands, string.format('certutil -f -decode %s %s >nul 2>nul', win_quote(tempB64), win_quote(dest_path)))
            table.insert(commands, string.format('powershell -NoLogo -NoProfile -Command "Try { $b=[IO.File]::ReadAllText(%s); [IO.File]::WriteAllBytes(%s,[Convert]::FromBase64String($b)); exit 0 } Catch { exit 1 }"', ps_quote(tempB64), ps_quote(dest_path)))
        else
            table.insert(commands, string.format('base64 -D -i %q -o %q 2>/dev/null', tempB64, dest_path))
            table.insert(commands, string.format('base64 -d %q > %q 2>/dev/null', tempB64, dest_path))
            table.insert(commands, string.format('openssl base64 -d -A -in %q -out %q 2>/dev/null', tempB64, dest_path))
        end

        local expected_min = nil
        if expected_bytes and expected_bytes > 0 then
            local min_ratio = 0.85
            if expected_bytes < 2048 then
                min_ratio = 0.6
            end
            expected_min = math.max(128, math.floor(expected_bytes * min_ratio))
        end

        local success = false
        local out_size = 0
        for _, cmd in ipairs(commands) do
            if Utils.fileExists(dest_path) then
                os.remove(dest_path)
            end
            if Utils.runShellCommand(cmd) and Utils.fileExists(dest_path) then
                local sz = Utils.getFileSize(dest_path)
                if sz > 0 then
                    if not expected_min or sz >= expected_min then
                        success = true
                        out_size = sz
                        break
                    end
                end
            end
        end
        os.remove(tempB64)
        if not success and Utils.fileExists(dest_path) then
            os.remove(dest_path)
        end
        return success, out_size
    end

    local function system_base64_decode_string(payload)
        if type(payload) ~= "string" or payload == "" then
            return ""
        end
        local tempOut, errOut = Utils.makeTempPath("bin")
        if not tempOut then
            return nil, errOut
        end
        local expected = estimate_decoded_size(payload)
        local ok, _ = system_decode_base64(payload, tempOut, expected)
        if ok then
            local bytes = Utils.readFile(tempOut) or ""
            os.remove(tempOut)
            return bytes
        end
        os.remove(tempOut)
        return nil, "system_decode_failed"
    end

    function Utils.base64Decode(b64)
        local payload = strip_data_url(b64 or "")
        payload = payload:gsub("[^%w%+/%=]", "")
        if payload == "" then
            return ""
        end
        local decoded = select(1, system_base64_decode_string(payload))
        if decoded then
            return decoded
        end
        return ""
    end

    function Utils.decodeBase64ToFile(dataUrl, dest_path)
        if type(dest_path) ~= "string" or dest_path == "" then
            return false, "invalid_dest"
        end
        local payload = strip_data_url(dataUrl or "")
        payload = payload:gsub("[^%w%+/%=]", "")
        if payload == "" then
            return false, "invalid_data_url"
        end
        local expected = estimate_decoded_size(payload)
        local sys_ok, sys_size = system_decode_base64(payload, dest_path, expected)
        if sys_ok then
            return true, sys_size
        end

        return false, "system_decode_failed"
    end

    -- 把图片文件编码为 data:URL（兼容 jpg/png）
    function Utils.encodeImageToBase64(imgPath)
        local blob = Utils.readFile(imgPath)
        if not blob then return nil end
        local ext  = (tostring(imgPath):lower():match("%.([^%.]+)$") or "png")
        local mime = (ext == "jpg" or ext == "jpeg") and "jpeg" or "png"
        return ("data:image/%s;base64,%s"):format(mime, Utils.base64Encode(blob))
    end

    function Utils.saveDataUrlImage(dataUrl, saveDir, prefix)
        if type(dataUrl) ~= "string" or dataUrl == "" then
            return nil, "invalid_data_url"
        end
        saveDir = Utils.trim(saveDir or "")
        if saveDir == "" then
            saveDir = Utils.getTempDir()
        end
        if not Utils.ensureDir(saveDir) then
            return nil, "save_dir_unavailable"
        end

        local mimeType = dataUrl:match("^data:image/([%w%-%+%.]+)")
        local ext = "png"
        if mimeType then
            local lowerMime = mimeType:lower()
            if lowerMime == "jpeg" or lowerMime == "jpg" then
                ext = "jpg"
            elseif lowerMime == "webp" then
                ext = "webp"
            elseif lowerMime == "gif" then
                ext = "gif"
            else
                ext = lowerMime:gsub("[^%w]", "")
                if ext == "" then ext = "png" end
            end
        end

        local prefixValue = Utils.sanitizeFilename(prefix or "image_edit_")
        if prefixValue == "" then
            prefixValue = "image_edit_"
        end
        local uniqueName = string.format("%s%d_%04d", prefixValue, os.time(), math.random(0, 9999))
        local filename = uniqueName .. "." .. ext
        local destPath = Utils.joinPath(saveDir, filename)
        local decode_ok, size_or_err = Utils.decodeBase64ToFile(dataUrl, destPath)
        if not decode_ok then
            os.remove(destPath)
            return nil, size_or_err or "decode_failed"
        end

        return destPath, size_or_err
    end

    function Utils.saveImageFromUrlAsync(imageUrl, saveDir, prefix, onComplete)
        if type(onComplete) ~= "function" then
            return false, "missing_callback"
        end
        if type(imageUrl) ~= "string" or imageUrl == "" then
            return false, "invalid_url"
        end
        local async = App and App.AsyncHttp
        if not (async and async.start and Utils.runShellCommandAsync) then
            return false, "async_unavailable"
        end
        if async.isActive and async.isActive() then
            return false, "async_busy"
        end

        saveDir = Utils.trim(saveDir or "")
        if saveDir == "" then
            saveDir = Utils.getTempDir()
        end
        if not Utils.ensureDir(saveDir) then
            return false, "save_dir_unavailable"
        end

        local ext = imageUrl:match("%.([%w%d]+)(%?.*)?$")
        if ext then
            ext = ext:lower()
            if ext == "jpeg" then ext = "jpg" end
            if ext == "" then ext = nil end
        end
        if not ext or #ext > 6 then
            ext = "png"
        end

        local prefixValue = Utils.sanitizeFilename(prefix or "image_edit_")
        if prefixValue == "" then
            prefixValue = "image_edit_"
        end
        local filename = string.format("%s%d_%04d.%s", prefixValue, os.time(), math.random(0, 9999), ext)
        local destPath = Utils.joinPath(saveDir, filename)

        local maxTime = 300
        local statusPath, errStatus = Utils.makeTempPath("status")
        local errPath, errErr = Utils.makeTempPath("err")
        if not statusPath or not errPath then
            if statusPath then os.remove(statusPath) end
            if errPath then os.remove(errPath) end
            return false, "tmp_status_failed"
        end
        local debugPaths = {
            status = statusPath,
            err = errPath
        }

        local configPath, cfgErr = Utils.writeCurlConfig({
            url = imageUrl,
            output = destPath
        })
        if not configPath then
            Utils.cleanupDebugPaths(debugPaths)
            return false, "curl_config_failed:" .. tostring(cfgErr)
        end
        debugPaths.config = configPath

        local cmd
        local redirected
        if Utils.IS_WINDOWS then
            cmd = string.format('curl -sS -L -f --max-time %d --config "%s" -w "%%%%{http_code}"', maxTime, configPath)
            redirected = string.format('%s > "%s" 2>"%s"', cmd, statusPath, errPath)
        else
            cmd = string.format('curl -sS -L -f --max-time %d --config %q -w "%%{http_code}"', maxTime, configPath)
            redirected = string.format('( %s ) > %q 2>%q', cmd, statusPath, errPath)
        end

        local spawned = Utils.runShellCommandAsync(redirected)
        if not spawned then
            Utils.cleanupDebugPaths(debugPaths)
            if Utils.fileExists(destPath) then
                os.remove(destPath)
            end
            return false, "download_failed"
        end

        local margin = (async and async.TIMEOUT_MARGIN) or 20
        local pollTimeout = math.max(5, maxTime + margin)
        local task = {
            kind = "download",
            outputPath = destPath,
            statusPath = statusPath,
            errPath = errPath,
            debugPaths = debugPaths,
            pollTimeout = pollTimeout,
            startedAt = os.time(),
            onComplete = function(result)
                local ok = result.ok == true
                local size = result.size or Utils.getFileSize(destPath) or 0
                if not ok or not size or size <= 0 then
                    if Utils.fileExists(destPath) then
                        os.remove(destPath)
                    end
                    if type(onComplete) == "function" then
                        onComplete(false, nil, "download_failed", debugPaths)
                    end
                    return
                end
                if type(onComplete) == "function" then
                    onComplete(true, destPath, size, debugPaths)
                end
            end
        }

        local started, startErr = async.start(task)
        if not started then
            Utils.cleanupDebugPaths(debugPaths)
            if Utils.fileExists(destPath) then
                os.remove(destPath)
            end
            return false, startErr or "async_start_failed"
        end
        return true
    end

    App.Utils = Utils
end

---
--- 3.1 异步 HTTP 轮询模块 (App.AsyncHttp)
---
do
    local AsyncHttp = {}
    local Core = App.Core
    local Utils = App.Utils

    AsyncHttp.TIMER_ID = "HttpPollingTimer"
    AsyncHttp.POLL_INTERVAL = 250
    AsyncHttp.TIMEOUT_MARGIN = 20

    AsyncHttp.state = {
        active = false,
        task = nil,
        startedAt = 0,
        lastSize = nil,
        stableTicks = 0,
    }

    local pollTimer

    function AsyncHttp.isActive()
        return AsyncHttp.state.active == true
    end

    function AsyncHttp.ensureTimer()
        if pollTimer then
            return pollTimer
        end
        if not (Core and Core.ui) then
            return nil
        end
        pollTimer = Core.ui:Timer{
            ID = AsyncHttp.TIMER_ID,
            Interval = AsyncHttp.POLL_INTERVAL,
            SingleShot = true,
            TimerType = "CoarseTimer",
        }
        return pollTimer
    end

    function AsyncHttp.startTimer()
        local t = AsyncHttp.ensureTimer()
        if t then
            pcall(function() t:Start() end)
        end
    end

    function AsyncHttp.stopTimer()
        if pollTimer then
            pcall(function() pollTimer:Stop() end)
        end
    end

    function AsyncHttp.start(task)
        if AsyncHttp.state.active then
            return false, "async_busy"
        end
        if type(task) ~= "table" then
            return false, "invalid_task"
        end
        if not AsyncHttp.ensureTimer() then
            return false, "timer_unavailable"
        end
        AsyncHttp.state.active = true
        AsyncHttp.state.task = task
        AsyncHttp.state.startedAt = os.time()
        AsyncHttp.state.lastSize = nil
        AsyncHttp.state.stableTicks = 0
        AsyncHttp.startTimer()
        return true
    end

    function AsyncHttp.finish(result)
        local task = AsyncHttp.state.task
        AsyncHttp.stopTimer()
        AsyncHttp.state.active = false
        AsyncHttp.state.task = nil
        AsyncHttp.state.lastSize = nil
        AsyncHttp.state.stableTicks = 0
        if task and type(task.onComplete) == "function" then
            pcall(task.onComplete, result or {})
        end
    end

    function AsyncHttp.cancel(reason)
        if not AsyncHttp.state.active then
            return false
        end
        local task = AsyncHttp.state.task
        AsyncHttp.stopTimer()
        AsyncHttp.state.active = false
        AsyncHttp.state.task = nil
        AsyncHttp.state.lastSize = nil
        AsyncHttp.state.stableTicks = 0
        if task and task.debugPaths then
            Utils.cleanupDebugPaths(task.debugPaths)
        end
        return true
    end

    local function read_status(path)
        if not path or not Utils.fileExists(path) then
            return nil, ""
        end
        local data = Utils.readFile(path) or ""
        local code = tonumber((data or ""):match("(%d%d%d)"))
        if code == 0 then code = nil end   -- 关键：把 000 当作"没拿到状态码"
        return code, data
    end

    local function read_err(path)
        if not path or not Utils.fileExists(path) then
            return "", 0
        end
        local size = Utils.getFileSize(path) or 0
        if size <= 0 then
            return "", size
        end
        return Utils.readFile(path) or "", size
    end

    local function is_stable_size(size)
        local state = AsyncHttp.state
        if state.lastSize ~= nil and size == state.lastSize then
            state.stableTicks = (state.stableTicks or 0) + 1
        else
            state.lastSize = size
            state.stableTicks = 0
        end
        return (state.stableTicks or 0) >= 1
    end

    local function poll_http(task, elapsed)
        local statusCode, statusData = read_status(task.statusPath)
        local statusReady = statusData and statusData ~= ""
        local bodyPath = task.bodyPath
        local size = 0
        if bodyPath and Utils.fileExists(bodyPath) then
            size = Utils.getFileSize(bodyPath) or 0
        end
        local stable = is_stable_size(size)
        local errText, errSize = read_err(task.errPath)
        local errHas = errSize and errSize > 0 and errText ~= ""
        if statusReady and stable then
            local body = ""
            if bodyPath and Utils.fileExists(bodyPath) then
                body = Utils.readFile(bodyPath) or ""
            end
            AsyncHttp.finish({
                ok = (body ~= ""),
                body = body,
                statusCode = statusCode,
                statusData = statusData,
                errText = errText,
                debugPaths = task.debugPaths,
            })
            return true
        end
        if errHas and stable and (statusReady or (elapsed or 0) >= 5) and size <= 0 then
            AsyncHttp.finish({
                ok = false,
                err = "curl_failed",
                statusCode = statusCode,
                statusData = statusData,
                errText = errText,
                debugPaths = task.debugPaths,
            })
            return true
        end
        -- 容错：curl 报错(例如 56)导致 -w 没写出 status，但 body 已经写出来了
        -- 只要 body 文件>0 且大小稳定，就结束任务，把 body 交给上层做"截断检测/提取"
        if (not statusReady) and errHas and stable and size > 0 and (elapsed or 0) >= 5 then
            local body = ""
            if bodyPath and Utils.fileExists(bodyPath) then
                body = Utils.readFile(bodyPath) or ""
            end
            AsyncHttp.finish({
                ok = (body ~= ""),
                body = body,
                statusCode = statusCode,     -- 很可能是 nil（因为 statusPath 没写出来）
                statusData = statusData,
                errText = errText,
                debugPaths = task.debugPaths,
                partial = true,              -- 可选：标记一下是容错路径
            })
            return true
        end
        return false
    end

    local function poll_download(task, elapsed)
        local statusCode, statusData = read_status(task.statusPath)
        local statusReady = statusData and statusData ~= ""
        local outputPath = task.outputPath
        local size = 0
        if outputPath and Utils.fileExists(outputPath) then
            size = Utils.getFileSize(outputPath) or 0
        end
        local stable = is_stable_size(size)
        local errText, errSize = read_err(task.errPath)
        local errHas = errSize and errSize > 0 and errText ~= ""
        if statusReady and stable then
            local ok = (statusCode >= 200 and statusCode < 300 and size > 0)
            if not statusCode and size > 0 then
                ok = true
            end
            AsyncHttp.finish({
                ok = ok,
                path = outputPath,
                size = size,
                statusCode = statusCode,
                statusData = statusData,
                errText = errText,
                debugPaths = task.debugPaths,
            })
            return true
        end
        if errHas and stable and (statusReady or (elapsed or 0) >= 5) and size <= 0 then
            AsyncHttp.finish({
                ok = false,
                err = "curl_failed",
                statusCode = statusCode,
                statusData = statusData,
                errText = errText,
                debugPaths = task.debugPaths,
            })
            return true
        end
        return false
    end

    function AsyncHttp.poll()
        local task = AsyncHttp.state.task
        if not task then
            return
        end
        local pollTimeout = tonumber(task.pollTimeout) or 0
        local elapsed = os.time() - (task.startedAt or AsyncHttp.state.startedAt or os.time())
        if pollTimeout > 0 then
            if elapsed >= pollTimeout then
                AsyncHttp.finish({
                    ok = false,
                    err = "timeout",
                    debugPaths = task.debugPaths,
                })
                return
            end
        end

        local done
        if task.kind == "download" then
            done = poll_download(task, elapsed)
        else
            done = poll_http(task, elapsed)
        end
        if not done and AsyncHttp.state.active then
            AsyncHttp.startTimer()
        end
    end

    if Core and Core.registerTimeoutHandler then
        Core.registerTimeoutHandler(AsyncHttp.TIMER_ID, function()
            AsyncHttp.poll()
            return true
        end)
    end
    App.AsyncHttp = AsyncHttp
end

