function App:Run()
    local Core = self.Core
    local UI = self.UI
    local Config = self.Config
    local Utils = self.Utils
    local json = require("dkjson")
    local Services = self.Services
    local Resolve = self.Resolve
    local is_google_provider = UI.is_google_provider
    local is_google_pro = UI.is_google_pro
    local get_provider_aspect_id = UI.get_provider_aspect_id
    local set_provider_aspect_id = UI.set_provider_aspect_id
    local get_provider_resolution_id = UI.get_provider_resolution_id
    local set_provider_resolution_id = UI.set_provider_resolution_id
    local get_generator_aspect_id = UI.get_generator_aspect_id
    local set_generator_aspect_id = UI.set_generator_aspect_id
    local get_generator_resolution_id = UI.get_generator_resolution_id
    local set_generator_resolution_id = UI.set_generator_resolution_id
    local resolution_options_by_provider = UI.resolution_options_by_provider
    local resolution_index_by_id = UI.resolution_index_by_id
    local function resolve_google_model(pid, cfg)
        local model = cfg and cfg.model
        if is_google_pro(pid) and (not model or model == "" or model == Config.GOOGLE_DEFAULT_MODEL) then
            model = Config.GOOGLE_PRO_DEFAULT_MODEL
        end
        if not model or model == "" then
            model = Config.GOOGLE_DEFAULT_MODEL
        end
        return model
    end

    local ProviderBase = {}
    ProviderBase.__index = ProviderBase
    function ProviderBase:new(def)
        def = def or {}
        setmetatable(def, self)
        return def
    end

    local Providers = {list = {}, by_id = {}}
    local function register_provider(p)
        table.insert(Providers.list, p)
        Providers.by_id[p.id] = p
    end

    local function make_google_provider(id, label, isPro)
        local provider = ProviderBase:new{
            id = id,
            label = label,
            is_pro = isPro,
            kind = "google"
        }
        function provider:get_config()
            return UI.config.google_gen or UI.config.google or {}
        end
        function provider:get_defaults()
            return Config.DEFAULT_CONFIG.google_gen or Config.DEFAULT_CONFIG.google
        end
        function provider:resolution_options()
            return isPro and UI.resolutionOptionsPro or UI.resolutionOptionsFlash
        end
        function provider:aspect_options()
            return UI.aspectRatioOptions
        end
        function provider:build_generation_request(promptText)
            local cfg = self:get_config()
            local baseUrl = Utils.trim((cfg and cfg.base_url) or "")
            if baseUrl == "" then
                baseUrl = Config.GOOGLE_DEFAULT_BASE_URL
            end
            local apiKey = Utils.trim((cfg and cfg.api_key) or "")
            if baseUrl == "" or apiKey == "" then
                if UI.google_config_win then UI.google_config_win:Show() end
                return nil, "needGoogleConfig"
            end
            local modelName = resolve_google_model(self.id, cfg)
            local requestUrl = Utils.buildGoogleEndpoint(baseUrl, modelName)
            if requestUrl == "" then
                return nil, "invalidBaseUrl"
            end
            local defaults = self:get_defaults()
            local aspectId = (cfg and cfg.aspect_ratio) or defaults.aspect_ratio
            local resId = (cfg and cfg.resolution) or defaults.resolution
            local imageConfig = {}
            if aspectId and aspectId ~= "" then
                imageConfig.aspectRatio = aspectId
            end
            if self.is_pro and resId and resId ~= "" then
                imageConfig.imageSize = resId
            end
            local generationConfig = {responseModalities = {"TEXT", "IMAGE"}}
            if next(imageConfig) then
                generationConfig.imageConfig = imageConfig
            end
            local headers = {
                ["x-goog-api-key"] = apiKey,
                ["Content-Type"] = "application/json",
                ["Accept"] = "application/json"
            }
            local payload = {
                model = modelName,
                contents = {{
                    role = "user",
                    parts = {
                        {text = promptText}
                    }
                }},
                generationConfig = generationConfig
            }
            return {
                url = requestUrl,
                headers = headers,
                payload = payload,
                aspectId = aspectId,
                resId = resId,
                model = modelName,
                baseUrl = baseUrl
            }
        end
        return provider
    end

    register_provider(make_google_provider("google", "Nano Banana 🍌", false))
    register_provider(make_google_provider("google_pro", "Nano Banana Pro 🍌", true))

    local providerOptions = {}
    for _, p in ipairs(Providers.list) do
        table.insert(providerOptions, {id = p.id, label = p.label})
    end
    if #providerOptions > 0 then
        UI.providerOptions = providerOptions
    end

    -- Reuse the existing provider_index_by_id from UI module
    local provider_index_by_id = UI._provider_index_by_id
    local provider_label_by_id = UI._provider_label_by_id
    local aspect_ratio_index_by_id = UI._aspect_ratio_index_by_id

    local TAB_INDEX_GENERATE = 0
    local TAB_INDEX_EDIT = 1
    local TAB_INDEX_SETTINGS = 2

    -- 构建主窗口
    UI.MainWin = UI.build_main_window()
    if not UI.MainWin then
        print(string.format("%s: 无法创建主窗口.", Config.SCRIPT_NAME))
        return
    end

    -- 获取 UI 元素
    local items = UI.MainWin:GetItems()
    UI.Items = items

    -- 创建服务商配置窗口
    UI.google_config_win = UI.create_google_config_window(Core, Config)

    local function current_google_config(_)
        return UI.config.google or {}
    end

    -- Generic combo refresh helper: populates a combo box with options and sets the current index
    local function populate_combo(combo, optionsList, selectedIndex)
        if not combo then return end
        if combo.Clear then combo:Clear() end
        for _, opt in ipairs(optionsList) do
            if combo.AddItem then combo:AddItem(opt.label) end
        end
        if combo.CurrentIndex ~= nil then
            combo.CurrentIndex = selectedIndex - 1
        end
    end

    -- Provider combo refresh factory
    local function make_provider_combo_refresher(comboId, configKey)
        return function()
            local combo = items[comboId]
            if not combo then return end
            local optsList = UI.providerOptions or {}
            if #optsList == 0 then return end
            local pid = UI.config[configKey] or "google"
            local idx = provider_index_by_id(pid)
            if idx > #optsList then
                idx = 1
                UI.config[configKey] = optsList[1].id
                Utils.saveConfig(UI.config)
            end
            populate_combo(combo, optsList, idx)
        end
    end

    -- Aspect ratio combo refresh factory
    local function make_aspect_ratio_combo_refresher(comboId, configKey, aspectIdGetter)
        return function(providerId)
            local combo = items[comboId]
            if not combo then return end
            local pid = providerId or (UI.config[configKey] or "google")
            local currentId = aspectIdGetter(pid) or UI.aspectRatioOptions[1].id
            local idx = aspect_ratio_index_by_id(currentId)
            populate_combo(combo, UI.aspectRatioOptions, idx)
        end
    end

    -- Resolution combo refresh factory
    local function make_resolution_combo_refresher(comboId, labelId, configKey, resolutionIdGetter)
        return function(providerId)
            local combo = items[comboId]
            if not combo then return end
            local pid = providerId or (UI.config[configKey] or "google")
            local opts = resolution_options_by_provider(pid)
            local resId = resolutionIdGetter(pid)
            local idx = resolution_index_by_id(pid, resId)
            populate_combo(combo, opts, idx)
            local enabled = is_google_provider(pid)
            combo.Enabled = enabled
            if items[labelId] then
                items[labelId].Enabled = enabled
            end
        end
    end

    -- Create refresh functions for Edit tab
    local refresh_provider_combo = make_provider_combo_refresher("ProviderCombo", "provider_choice")
    local refresh_aspect_ratio_combo = make_aspect_ratio_combo_refresher(
        "AspectRatioCombo",
        "provider_choice",
        function(pid) return get_provider_aspect_id(pid) end
    )
    local refresh_resolution_combo = make_resolution_combo_refresher(
        "ResolutionCombo",
        "ResolutionLabel",
        "provider_choice",
        function(pid) return get_provider_resolution_id(pid) end
    )

    -- Create refresh functions for Generate tab
    local refresh_gen_provider_combo = make_provider_combo_refresher("GenProviderCombo", "generator_provider_choice")
    local refresh_gen_aspect_ratio_combo = make_aspect_ratio_combo_refresher(
        "GenAspectRatioCombo",
        "generator_provider_choice",
        function(pid) return get_generator_aspect_id(pid) end
    )
    local refresh_gen_resolution_combo = make_resolution_combo_refresher(
        "GenResolutionCombo",
        "GenResolutionLabel",
        "generator_provider_choice",
        function(pid) return get_generator_resolution_id(pid) end
    )

    refresh_provider_combo()
    refresh_aspect_ratio_combo(UI.config.provider_choice)
    refresh_resolution_combo(UI.config.provider_choice)
    refresh_gen_provider_combo()
    refresh_gen_aspect_ratio_combo(UI.config.generator_provider_choice)
    refresh_gen_resolution_combo(UI.config.generator_provider_choice)

    -- 填充 Tab
    for _, tab_name in ipairs(UI.translations[UI.currentLang].Tabs) do
        items.MainTabs:AddTab(tab_name)
    end

    if items.MainStack then
        items.MainTabs.CurrentIndex = 0
        items.MainStack.CurrentIndex = 0
    end

    local lastTabIndex = (items.MainTabs and items.MainTabs.CurrentIndex) or 0

    -- 设置事件处理程序
    if UI.MainWin and UI.MainWin.On then
        -- Tab 切换
        UI.MainWin.On.MainTabs.CurrentChanged = function(ev)
            local idx = (ev and (ev.Index or ev.index)) or 0
            local prev = lastTabIndex
            lastTabIndex = idx
            if items.MainStack then
                items.MainStack.CurrentIndex = idx
            end
            if prev == TAB_INDEX_GENERATE and idx == TAB_INDEX_EDIT then
                if UI.generatedImagePath and UI.generatedImagePath ~= "" then
                    if loadImageToSlot then
                        loadImageToSlot(UI.generatedImagePath, 1)
                    else
                        UI.currentImagePaths[1] = UI.generatedImagePath
                        if items.OriginalImagePreview1 then
                            items.OriginalImagePreview1.Icon = Core.ui.Icon{File = UI.generatedImagePath}
                            items.OriginalImagePreview1.IconSize = {120, 120}
                            items.OriginalImagePreview1.Text = ""
                        end
                    end
                end
            end
        end

        local function get_message(section, key)
            local bucket = UI.messages and UI.messages[section]
            if bucket then
                return bucket[key]
            end
            return nil
        end

        local function localizedText(textOrMap)
            if type(textOrMap) == "table" then
                local lang = (UI.currentLang == "en") and "en" or "cn"
                return textOrMap[lang] or textOrMap.cn or textOrMap.en or ""
            end
            return tostring(textOrMap or "")
        end

        local function updateStatus(message)
            local final = localizedText(message)
            if items.StatusLabel then
                items.StatusLabel.Text = final
            end
            if items.GenStatusLabel then
                items.GenStatusLabel.Text = final
            end
            print("[STATUS] " .. final)
        end
        UI.updateStatus = updateStatus

        local function set_network_busy(busy)
            UI.networkBusy = busy and true or false
            local enabled = not UI.networkBusy
            if items.GenGenerateBtn then
                items.GenGenerateBtn.Enabled = enabled
            end
            if items.GenerateBtn then
                items.GenerateBtn.Enabled = enabled
            end
        end

        local function is_network_busy()
