-- 为 SiliconFlow 构建请求体 (OpenAI 兼容格式)
function SiliconFlowService.buildRequestPayload(sentence, targetLabel)
    -- 使用简化的专用提示
    local prompt = SILICONFLOUW_SYSTEM_PROMPT:gsub("{target_lang}", targetLabel or "English")
    
    local messages = {
        { role = "system", content = prompt },
        -- 您的要求是“只用传入 text 参数”，这里我将其理解为 User Message 的内容
        { role = "user", content = sentence or "" } 
    }

    local payloadTable = {
        model = SILICONFLOUW_MODEL,
        messages = messages,
        temperature = SILICONFLOUW_TEMPERATURE,
    }
    return json.encode(payloadTable)
end

-- 解析 SiliconFlow 响应 (复用 GLM 的解析器)
function SiliconFlowService.parseResponseBody(body)
    return GLMService.parseResponseBody(body) -- 假设响应格式与 GLM/OpenAI 相同
end

-- 请求 SiliconFlow 翻译 (单次)
function SiliconFlowService.requestTranslation(sentence, targetLabel, apiKey)
    local payload = SiliconFlowService.buildRequestPayload(sentence, targetLabel)
    local headers = {
        Authorization = "Bearer " .. apiKey,
        ["Content-Type"] = "application/json",
        ["User-Agent"] = string.format("%s/%s", SCRIPT_NAME, SCRIPT_VERSION),
    }

    local body, status = Services.httpPostJson(SILICONFLOUW_API_URL, payload, headers, SILICONFLOUW_TIMEOUT)
    if not body then
        return nil, 0, status or "request_failed"
    end

    return SiliconFlowService.parseResponseBody(body)
end

-- 翻译 SiliconFlow 条目 (免费版专用)
function SiliconFlowService.translateEntries(entries, targetLabel)
    if not entries or #entries == 0 then
        return nil, "no_entries"
    end

    local apiKey, fetchErr = fetch_provider_secret(SILICONFLOUW_SUPABASE_PROVIDER)
    if not apiKey then
        return nil, fetchErr or "missing_key"
    end

    local totalTokens = 0
    local total = #entries
    local processed = 0
    
    -- 免费版强制串行 (concurrency = 1)
    setTranslateStatus("progress", processed, total, totalTokens)

    for index, entry in ipairs(entries) do
        local success = false
        local lastError = nil

        for attempt = 1, SILICONFLOUW_MAX_RETRY do
            local translation, tokens, errMsg = SiliconFlowService.requestTranslation(entry.original or "", targetLabel, apiKey)
            if translation then
                entry.translation = translation
                updateTranslateTreeRow(index, translation)
                totalTokens = totalTokens + (tokens or 0)
                success = true
                break
            else
                lastError = errMsg
            end
        end

        processed = processed + 1
        setTranslateStatus("progress", processed, total, totalTokens)

        if not success then
            local fallback = string.format("[Error: %s]", tostring(lastError or "failed"))
            entry.translation = fallback
            updateTranslateTreeRow(index, fallback)
        end
    end

    return totalTokens, nil
end


-- 修改：翻译主流程，增加免费版限制检查
local function performTranslateWorkflow()
    if state.translate.busy then
        return false
    end

    ensureTranslateEntries(false)
    local entries = state.translate.entries or {}
    if #entries == 0 then
        setTranslateStatus("no_entries")
        return false
    end

    local provider = it.TranslateProviderCombo and it.TranslateProviderCombo.CurrentText or state.translate.provider
    if not provider or Utils.trim(provider) == "" then
        provider = TRANSLATE_PROVIDER_SILICONFLOUW_LABEL
    end
    state.translate.provider = provider
    
    local targetLabel = it.TranslateTargetCombo and it.TranslateTargetCombo.CurrentText or state.translate.targetLabel or "English"
    state.translate.targetLabel = targetLabel

    local concurrencyValue = state.translate.concurrency or DEFAULT_TRANSLATE_CONCURRENCY

    -- ==================================
    -- 免费版限制检查 (开始)
    -- ==================================
    if provider ~= TRANSLATE_PROVIDER_SILICONFLOUW_LABEL then
        show_upgrade_message("upgrade_provider")
        setTranslateStatus("failed", resolveTranslateError("upgrade_provider"))
        return false
    end

    if not FREE_LANG_LIST[targetLabel] then
        show_upgrade_message("upgrade_language")
        setTranslateStatus("failed", resolveTranslateError("upgrade_language"))
        return false
    end

    if concurrencyValue > 1 then
        show_upgrade_message("upgrade_concurrency")
        setTranslateStatus("failed", resolveTranslateError("upgrade_concurrency"))
        return false
    end
    -- ==================================
    -- 免费版限制检查 (结束)
    -- ==================================

    -- 免费版只允许 SiliconFlow
    local translateFunc = SiliconFlowService.translateEntries
    
    state.translate.busy = true
    setTranslateControlsEnabled(false)

    local ok, totalTokens, err = pcall(translateFunc, entries, targetLabel)

    state.translate.busy = false
    setTranslateControlsEnabled(true)

    if not ok then
        state.translate.totalTokens = 0
        local en, cn = resolveTranslateErrorPair(totalTokens or "translation_failed")
        show_dynamic_message(en, cn)
        setTranslateStatus("failed", resolveTranslateError(totalTokens) or tostring(totalTokens))
        return false
    end

    if not totalTokens then
        state.translate.totalTokens = 0
        local en, cn = resolveTranslateErrorPair(err or "translation_failed")
        show_dynamic_message(en, cn)
        setTranslateStatus("failed", resolveTranslateError(err or "translation_failed"))
        return false
    end

    state.translate.totalTokens = totalTokens
    setTranslateStatus("success", #entries, totalTokens)
    notifyTranslateStatus("success", { #entries, totalTokens }, { #entries, totalTokens })
    return true
end

-- 修改：翻译单条，增加免费版限制检查
local function translateSingleEntry(index, targetLabel)

    local entries = state.translate.entries or {}
    local entry = entries[index]
    if not entry then
        setTranslateStatus("failed", resolveTranslateError("no_selection"))
        return false
    end
    
    local provider = state.translate.provider or TRANSLATE_PROVIDER_SILICONFLOUW_LABEL

    -- ==================================
    -- 免费版限制检查 (开始)
    -- ==================================
    if provider ~= TRANSLATE_PROVIDER_SILICONFLOUW_LABEL then
        show_upgrade_message("upgrade_provider")
        setTranslateStatus("failed", resolveTranslateError("upgrade_provider"))
        return false
    end

    if not FREE_LANG_LIST[targetLabel] then
        show_upgrade_message("upgrade_language")
        setTranslateStatus("failed", resolveTranslateError("upgrade_language"))
        return false
    end
    -- ==================================
    -- 免费版限制检查 (结束)
    -- ==================================

    local apiKey, fetchErr = fetch_provider_secret(SILICONFLOUW_SUPABASE_PROVIDER)
    if not apiKey then
        setTranslateStatus("failed", resolveTranslateError(fetchErr or "missing_key"))
        return false
    end

    setTranslateStatus("progress", 0, 1, state.translate.totalTokens or 0)

    local translation, tokens, errMsg = SiliconFlowService.requestTranslation(entry.original or "", targetLabel, apiKey)
    
    if translation then
        entry.translation = translation
        updateTranslateTreeRow(index, translation)
        state.translate.totalTokens = (state.translate.totalTokens or 0) + (tokens or 0)
        setTranslateStatus("success", 1, state.translate.totalTokens)
        return true
    end

    local fallback = string.format("[Error: %s]", tostring(errMsg or "failed"))
    entry.translation = fallback
    updateTranslateTreeRow(index, fallback)
    setTranslateStatus("success", 1, state.translate.totalTokens)
    return true
end

