        currentText = TRANSLATE_PROVIDER_SILICONFLOUW_LABEL
    end

    if currentText ~= TRANSLATE_PROVIDER_SILICONFLOUW_LABEL then
        -- 用户选择了无效的服务商
        show_upgrade_message("upgrade_provider")
        
        -- 自动切回免费版默认服务商
        local freeIndex = 0
        for idx, label in ipairs(TRANSLATE_PROVIDER_LIST) do
            if label == TRANSLATE_PROVIDER_SILICONFLOUW_LABEL then
                freeIndex = idx - 1
                break
            end
        end
        
        it.TranslateProviderCombo.CurrentIndex = freeIndex
        state.translate.provider = TRANSLATE_PROVIDER_SILICONFLOUW_LABEL
    else
        -- 用户选择了有效的服务商
        state.translate.provider = currentText
    end
end

function win.On.TranslateConcurrencyCombo.CurrentIndexChanged(ev)
    if not it.TranslateConcurrencyCombo then
        return
    end
    local index = it.TranslateConcurrencyCombo.CurrentIndex
    if not index or index < 0 then
        state.translate.concurrency = DEFAULT_TRANSLATE_CONCURRENCY
        setConcurrencyComboSelection(state.translate.concurrency)
        return
    end
    local option = TRANSLATE_CONCURRENCY_OPTIONS[index + 1]
    local value = option and option.value or DEFAULT_TRANSLATE_CONCURRENCY
    state.translate.concurrency = value
    
    -- 增加：如果选择高速，立即提示升级（但实际执行时仍会检查）
    if value > 1 then
        show_upgrade_message("upgrade_concurrency")
        -- 自动切回低速
        state.translate.concurrency = DEFAULT_TRANSLATE_CONCURRENCY
        setConcurrencyComboSelection(DEFAULT_TRANSLATE_CONCURRENCY)
    end
end

function win.On.TranslateTargetCombo.CurrentIndexChanged(ev)
    if not it.TranslateTargetCombo then
        return
    end
    
    local currentText = it.TranslateTargetCombo.CurrentText
    local defaultFreeLang = "English" -- 默认回退的免费语言
    
    if not currentText or currentText == "" then
        currentText = defaultFreeLang 
    end

    if not FREE_LANG_LIST[currentText] then
        -- 用户选择了无效的语言
        show_upgrade_message("upgrade_language")
        
        -- 自动切回免费版默认语言 (例如 "English")
        local freeIndex = 0
        local labels = Translate.getLangLabels()
        
        for idx, label in ipairs(labels) do
            if label == defaultFreeLang then
                freeIndex = idx - 1
                break
            end
        end
        
        it.TranslateTargetCombo.CurrentIndex = freeIndex
        state.translate.targetLabel = defaultFreeLang
    else
        -- 用户选择了有效的语言
        state.translate.targetLabel = currentText
    end
end

